$(".ajax_request").on("click",function(){var url=$(this).data("url");$.ajax({url:url,dataType:"html",success:function(data){addPopUpForm(data)}})});function addPopUpForm(data){$("body").append('<div class="overlay"></div>');$("body").append(data);var width=$(".form_popup_call").width();var height=$(".form_popup_call").height();var popup=jQuery(".form_popup_call");var leftPosition=(window.document.documentElement.offsetWidth-width)/2;var windowHeight=window.innerHeight||window.document.documentElement.offsetHeight;var topPosition=jQuery(window.document.documentElement).scrollTop()||jQuery(window.document).scrollTop();topPosition=topPosition+(windowHeight-height)/7;popup.css({left:leftPosition+"px",top:"200px"});$(".close").on("click",function(){$(".overlay").remove();$(".form_popup_call").remove()})}